{
    let topics = ["general"]; // Note for editor: Edit this for topics

    // Leave the code below as is. Please don't edit it.
    let topicArray = state.faqList.data.items.filter((item) => 
        item.tags.some(tag => topics.includes(tag))
    );
    let itemIndex = topicArray.findIndex((item) => item.question === state.itemsItem.question);
    let showConditionEven = (itemIndex + 1) % 2 === 0; // shows even index-items only, for right column
    let showConditionOdd = (itemIndex + 1) % 2 === 1; // shows odd index-items only, for left column

    return state.itemsItem.tags.some(tag => topics.includes(tag)) && showConditionOdd;
}

